// Configuration
// Try local stats.json first (for Plesk/open_basedir restrictions), then fallback to system location
$stats_files = [
    __DIR__ . '/stats.json',           // Local copy (Plesk-friendly)
    '/var/lib/ipdeny/stats.json'       // System location
];
define('REFRESH_INTERVAL', 60); // seconds

// Security: Read-only mode
header('X-Content-Type-Options: nosniff');
header('X-Frame-Options: DENY');
header('X-XSS-Protection: 1; mode=block');

// Load statistics from first available file
$stats = [];
$stats_file_used = null;
foreach ($stats_files as $file) {
    if (@file_exists($file) && is_readable($file)) {
        $stats = json_decode(file_get_contents($file), true) ?? [];
        $stats_file_used = $file;
        break;
    }
}

$last_updated = isset($stats['timestamp']) ? date('Y-m-d H:i:s', $stats['timestamp']) : 'Never';
$ipsets = $stats['ipsets'] ?? [];
$summary = $stats['summary'] ?? [];
?>
